import React, { useState } from 'react';
import { Container, Form, Button, Row, Col, Spinner, Nav, Tab, Toast } from 'react-bootstrap';
import InputMask from 'react-input-mask';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import NavigationBar from '../components/Navbar';
import '../styles/CadastroDoador.css';
import '../styles/CadastroBanco.css';
import { registrarBancoSangue } from '../services/api';

function CadastroBanco() {
  const navigate = useNavigate();
  const [validated, setValidated] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState({ type: '', message: '' });
  const [formData, setFormData] = useState({
    nome_organizacao: '',
    responsavel: '',
    cnpj: '',
    razao_social: '',
    site: '',
    senha: '',
    confirmarSenha: '',
    cep: '',
    cidade: '',
    estado: '',
    rua: '',
    numero: '',
    bairro: '',
    contato: '',
    email: ''
  });

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [id]: value
    }));
  };

  const consultaCEP = async (cep) => {
    if (cep.replace(/\D/g, '').length === 8) {
      setIsLoading(true);
      try {
        const response = await axios.get(`https://viacep.com.br/ws/${cep.replace(/\D/g, '')}/json/`);
        if (!response.data.erro) {
          const novoEndereco = {
            rua: response.data.logradouro,
            bairro: response.data.bairro,
            cidade: response.data.localidade,
            estado: response.data.uf
          };
          
          setFormData(prev => ({
            ...prev,
            ...novoEndereco
          }));
        }
      } catch (error) {
        console.error('Erro ao consultar CEP:', error);
        setToastMessage({
          type: 'danger',
          message: 'Erro ao consultar CEP. Tente novamente.'
        });
        setShowToast(true);
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    
    if (form.checkValidity() === false) {
      event.stopPropagation();
      setValidated(true);
      return;
    }

    if (formData.senha !== formData.confirmarSenha) {
      setToastMessage({
        type: 'danger',
        message: 'As senhas não coincidem!'
      });
      setShowToast(true);
      return;
    }

    setIsLoading(true);
    try {
      const dadosCadastro = {
        ...formData,
        cnpj: formData.cnpj.replace(/\D/g, ''),
        contato: formData.contato.replace(/\D/g, ''),
        cep: formData.cep.replace(/\D/g, '')
      };
      
      delete dadosCadastro.confirmarSenha;

      await registrarBancoSangue(dadosCadastro);
      
      setToastMessage({
        type: 'success',
        message: 'Banco de sangue cadastrado com sucesso!'
      });
      setShowToast(true);
      
      // Redirecionar após 2 segundos
      setTimeout(() => {
        navigate('/login');
      }, 1500);
    } catch (error) {
      console.error('Erro ao cadastrar:', error);
      setToastMessage({
        type: 'danger',
        message: error.response?.data?.error || error.message || 'Erro ao realizar cadastro. Tente novamente.'
      });
      setShowToast(true);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <NavigationBar />
      <Container className="py-5">
        <Toast
          show={showToast}
          onClose={() => setShowToast(false)}
          style={{
            position: 'fixed',
            top: 20,
            right: 20,
            zIndex: 9999
          }}
          bg={toastMessage.type}
          text={toastMessage.type === 'danger' ? 'white' : 'dark'}
          delay={3000}
          autohide
        >
          <Toast.Body>{toastMessage.message}</Toast.Body>
        </Toast>

        <Row className="justify-content-center">
          <Col md={8} lg={6}>
            <div className="register-container">
              <h2 className="text-center mb-4">Cadastro do Banco de Sangue</h2>
              <Form noValidate validated={validated} onSubmit={handleSubmit}>
                <Tab.Container id="cadastro-banco-tabs" defaultActiveKey="dados">
                  <Nav variant="pills" className="mb-4">
                    <Nav.Item>
                      <Nav.Link eventKey="dados" className="d-flex align-items-center">
                        <i className="fa-solid fa-address-book me-2"></i>
                        Dados da Organização
                      </Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="endereco" className="d-flex align-items-center">
                        <i className="fa-solid fa-location-dot me-2"></i>
                        Endereço e Contato
                      </Nav.Link>
                    </Nav.Item>
                  </Nav>

                  <Tab.Content>
                    <Tab.Pane eventKey="dados">
                      <Row className="mb-3">
                        <Form.Group as={Col} md="12" controlId="nome_organizacao">
                          <Form.Label>Nome da Organização</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            placeholder="Nome da organização"
                            value={formData.nome_organizacao}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>

                      <Row className="mb-3">
                        <Form.Group as={Col} md="12" controlId="responsavel">
                          <Form.Label>Responsável</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            placeholder="Nome do responsável"
                            value={formData.responsavel}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>

                      <Row className="mb-3">
                        <Form.Group as={Col} md="6" controlId="cnpj">
                          <Form.Label>CNPJ</Form.Label>
                          <InputMask
                            mask="99.999.999/9999-99"
                            className="form-control form-field"
                            required
                            type="text"
                            placeholder="00.000.000/0000-00"
                            value={formData.cnpj}
                            onChange={handleInputChange}
                          />
                        </Form.Group>

                        <Form.Group as={Col} md="6" controlId="razao_social">
                          <Form.Label>Razão Social</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            placeholder="Razão social"
                            value={formData.razao_social}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>

                      <Row className="mb-3">
                        <Form.Group as={Col} md="12" controlId="site">
                          <Form.Label>Site</Form.Label>
                          <Form.Control
                            type="url"
                            placeholder="https://www.exemplo.com.br"
                            value={formData.site}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>

                      <Row className="mb-3">
                        <Form.Group as={Col} md="6" controlId="senha">
                          <Form.Label>Senha</Form.Label>
                          <Form.Control
                            required
                            type="password"
                            placeholder="Digite sua senha"
                            value={formData.senha}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>

                        <Form.Group as={Col} md="6" controlId="confirmarSenha">
                          <Form.Label>Confirmar senha</Form.Label>
                          <Form.Control
                            required
                            type="password"
                            placeholder="Confirme sua senha"
                            value={formData.confirmarSenha}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>
                    </Tab.Pane>

                    <Tab.Pane eventKey="endereco">
                      <Row className="mb-3">
                        <Form.Group as={Col} md="4" controlId="cep">
                          <Form.Label>CEP</Form.Label>
                          <div className="position-relative">
                            <InputMask
                              mask="99999-999"
                              className="form-control form-field"
                              required
                              type="text"
                              placeholder="00000-000"
                              value={formData.cep}
                              onChange={(e) => handleInputChange({
                                target: {
                                  id: 'cep',
                                  value: e.target.value
                                }
                              })}
                              onBlur={(e) => consultaCEP(e.target.value)}
                              disabled={isLoading}
                            />
                            {isLoading && (
                              <div 
                                className="position-absolute"
                                style={{ 
                                  right: '10px',
                                  top: '50%',
                                  transform: 'translateY(-50%)'
                                }}
                              >
                                <Spinner
                                  animation="border"
                                  size="sm"
                                  variant="secondary"
                                />
                              </div>
                            )}
                          </div>
                        </Form.Group>

                        <Form.Group as={Col} md="4" controlId="cidade">
                          <Form.Label>Cidade</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            placeholder="Cidade"
                            value={formData.cidade}
                            onChange={handleInputChange}
                            disabled={isLoading}
                            className="form-field"
                          />
                        </Form.Group>

                        <Form.Group as={Col} md="4" controlId="estado">
                          <Form.Label>Estado</Form.Label>
                          <Form.Select
                            required
                            value={formData.estado}
                            onChange={handleInputChange}
                            disabled={isLoading}
                            className="form-field"
                          >
                            <option value="">Selecione</option>
                            <option value="AC">Acre</option>
                            <option value="AL">Alagoas</option>
                            <option value="AP">Amapá</option>
                            <option value="AM">Amazonas</option>
                            <option value="BA">Bahia</option>
                            <option value="CE">Ceará</option>
                            <option value="DF">Distrito Federal</option>
                            <option value="ES">Espírito Santo</option>
                            <option value="GO">Goiás</option>
                            <option value="MA">Maranhão</option>
                            <option value="MT">Mato Grosso</option>
                            <option value="MS">Mato Grosso do Sul</option>
                            <option value="MG">Minas Gerais</option>
                            <option value="PA">Pará</option>
                            <option value="PB">Paraíba</option>
                            <option value="PR">Paraná</option>
                            <option value="PE">Pernambuco</option>
                            <option value="PI">Piauí</option>
                            <option value="RJ">Rio de Janeiro</option>
                            <option value="RN">Rio Grande do Norte</option>
                            <option value="RS">Rio Grande do Sul</option>
                            <option value="RO">Rondônia</option>
                            <option value="RR">Roraima</option>
                            <option value="SC">Santa Catarina</option>
                            <option value="SP">São Paulo</option>
                            <option value="SE">Sergipe</option>
                            <option value="TO">Tocantins</option>
                          </Form.Select>
                        </Form.Group>
                      </Row>

                      <Row className="mb-3">
                        <Form.Group as={Col} md="8" controlId="rua">
                          <Form.Label>Rua</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            placeholder="Rua"
                            value={formData.rua}
                            onChange={handleInputChange}
                            disabled={isLoading}
                            className="form-field"
                          />
                        </Form.Group>

                        <Form.Group as={Col} md="4" controlId="numero">
                          <Form.Label>Número</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            placeholder="Número"
                            value={formData.numero}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>

                      <Row className="mb-3">
                        <Form.Group as={Col} md="12" controlId="bairro">
                          <Form.Label>Bairro</Form.Label>
                          <Form.Control
                            required
                            type="text"
                            placeholder="Bairro"
                            value={formData.bairro}
                            onChange={handleInputChange}
                            disabled={isLoading}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>

                      <Row className="mb-3">
                        <Form.Group as={Col} md="6" controlId="contato">
                          <Form.Label>Contato</Form.Label>
                          <InputMask
                            mask="(99) 9999-9999"
                            className="form-control form-field"
                            required
                            type="tel"
                            placeholder="(00) 0000-0000"
                            value={formData.contato}
                            onChange={handleInputChange}
                          />
                        </Form.Group>

                        <Form.Group as={Col} md="6" controlId="email">
                          <Form.Label>E-mail</Form.Label>
                          <Form.Control
                            required
                            type="email"
                            placeholder="exemplo@email.com"
                            value={formData.email}
                            onChange={handleInputChange}
                            className="form-field"
                          />
                        </Form.Group>
                      </Row>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>

                <div className="d-grid mt-4">
                  <Button 
                    type="submit"
                    variant="dark"
                    size="lg"
                    className="register-button"
                    disabled={isLoading}
                  >
                    {isLoading ? (
                      <>
                        <Spinner
                          as="span"
                          animation="border"
                          size="sm"
                          role="status"
                          aria-hidden="true"
                          className="me-2"
                        />
                        Cadastrando...
                      </>
                    ) : (
                      'Cadastrar'
                    )}
                  </Button>
                </div>
              </Form>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default CadastroBanco;
