import React from 'react';
import { Navbar, Nav, Container, Button, NavDropdown } from 'react-bootstrap';
import { useNavigate, useLocation } from 'react-router-dom';
import logo from '../assets/images/logo.png';
import '../styles/Navbar.css';

function NavigationBar() {
  const navigate = useNavigate();
  const location = useLocation();
  const isAuthenticated = localStorage.getItem('userId');
  const userType = localStorage.getItem('userType');

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userType');
    navigate('/login');
  };

  return (
    <Navbar bg="white" expand="lg" className="shadow-sm">
      <Container>
        <Navbar.Brand href="/">
          <img
            src={logo}
            height="40"
            className="d-inline-block align-top"
            alt="HemoGraph Logo"
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto align-items-center">
            <Nav.Link 
              href="/" 
              className={location.pathname === '/' ? 'active' : ''}
            >
              Home
            </Nav.Link>
            {userType === 'banco' ? (
              <>
                <Nav.Link 
                  href="/necessidades" 
                  className={location.pathname === '/necessidades' ? 'active' : ''}
                >
                  Encontrar Sangue
                </Nav.Link>
                <Nav.Link 
                  href="/gerenciar-necessidades"
                  className={location.pathname === '/gerenciar-necessidades' ? 'active' : ''}
                >
                  Gerenciar Necessidades
                </Nav.Link>
              </>
            ) : (
              <Nav.Link 
                href="/necessidades"
                className={location.pathname === '/necessidades' ? 'active' : ''}
              >
                Quero Doar
              </Nav.Link>
            )}
            {!isAuthenticated ? (
              <>
                <NavDropdown 
                  title="Cadastre-se" 
                  id="basic-nav-dropdown"
                  className={`nav-dropdown nav-link ${location.pathname === '/cadastro' ? 'active' : ''}`}
                >
                  <NavDropdown.Item onClick={() => navigate('/cadastro/doador')}>
                    Seja um doador
                  </NavDropdown.Item>
                  <NavDropdown.Item onClick={() => navigate('/cadastro/banco')}>
                    Cadastrar banco
                  </NavDropdown.Item>
                </NavDropdown>

                <Button 
                  variant="outline-primary" 
                  className="ms-3 nav-button" 
                  onClick={() => navigate('/login')}
                >
                  Entrar
                </Button>
              </>
            ) : (
              <Button 
                variant="outline-danger" 
                className="ms-3 nav-button"
                onClick={handleLogout}
              >
                Sair
              </Button>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
