import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/images/logo.png';

const navStyles = {
  navLink: {
    color: '#333',
    fontWeight: 500,
    transition: 'color 0.3s ease',
  },
  navLinkHover: {
    color: '#46052d',
  },
  button: {
    borderRadius: '4px',
    padding: '0.5rem 1.5rem',
  }
};

function NavigationBar() {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('userId');
  const userType = localStorage.getItem('userType');

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userType');
    navigate('/login');
  };

  return (
    <Navbar bg="white" expand="lg" className="shadow-sm py-3">
      <Container>
        <Navbar.Brand href="/">
          <img
            src={logo}
            height="40"
            className="d-inline-block align-top"
            alt="HemoGraph Logo"
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/" style={navStyles.navLink}>Home</Nav.Link>
            <Nav.Link href="/necessidades" style={navStyles.navLink}>Necessidades de Sangue</Nav.Link>
            {userType === 'banco' && (
              <Nav.Link href="/gerenciar-necessidades" style={navStyles.navLink}>
                Gerenciar Necessidades
              </Nav.Link>
            )}
          </Nav>
          <Nav>
            {!isAuthenticated ? (
              <>
                <Button 
                  variant="outline-primary" 
                  className="me-2" 
                  onClick={() => navigate('/login')}
                  style={navStyles.button}
                >
                  Entrar
                </Button>
                <Button 
                  variant="primary"
                  onClick={() => navigate('/cadastro/doador')}
                  style={navStyles.button}
                >
                  Cadastrar-se
                </Button>
              </>
            ) : (
              <Button 
                variant="outline-danger" 
                onClick={handleLogout}
                style={navStyles.button}
              >
                Sair
              </Button>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
