import React, { useState, useEffect } from 'react';
import { Container, Card, Badge, Button, Row, Col, Form, Toast } from 'react-bootstrap';
import { listarNecessidadesSangue, buscarNecessidadesPorTipo } from '../services/api';
import NavigationBar from '../components/Navbar';
import '../styles/ListaNecessidades.css';

function ListaNecessidades() {
  const [necessidades, setNecessidades] = useState([]);
  const [filtroTipo, setFiltroTipo] = useState('');
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState({ type: '', message: '' });
  const [isLoading, setIsLoading] = useState(true);

  const carregarNecessidades = async () => {
    try {
      setIsLoading(true);
      const response = filtroTipo
        ? await buscarNecessidadesPorTipo(filtroTipo)
        : await listarNecessidadesSangue();
      setNecessidades(response.necessidades);
    } catch (error) {
      setToastMessage({
        type: 'danger',
        message: 'Erro ao carregar necessidades de sangue'
      });
      setShowToast(true);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    carregarNecessidades();
  }, [filtroTipo]);

  const getBadgeVariant = (urgencia) => {
    switch (urgencia) {
      case 'CRITICA': return 'danger';
      case 'ALTA': return 'warning';
      case 'MEDIA': return 'info';
      case 'BAIXA': return 'success';
      default: return 'secondary';
    }
  };

  const formatarData = (data) => {
    if (!data) return 'Sem data limite';
    return new Date(data).toLocaleDateString('pt-BR');
  };

  return (
    <div>
      <NavigationBar />
      <Container className="py-5">
        <Toast
          show={showToast}
          onClose={() => setShowToast(false)}
          style={{
            position: 'fixed',
            top: 20,
            right: 20,
            zIndex: 9999
          }}
          bg={toastMessage.type}
          text={toastMessage.type === 'danger' ? 'white' : 'dark'}
          delay={3000}
          autohide
        >
          <Toast.Body>{toastMessage.message}</Toast.Body>
        </Toast>

        <div className="header-container mb-4">
          <h2>Necessidades de Sangue</h2>
          <Form.Select
            value={filtroTipo}
            onChange={(e) => setFiltroTipo(e.target.value)}
            style={{ width: '200px' }}
          >
            <option value="">Todos os tipos</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </Form.Select>
        </div>

        {isLoading ? (
          <div className="text-center">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Carregando...</span>
            </div>
          </div>
        ) : necessidades.length === 0 ? (
          <div className="text-center">
            <p>Nenhuma necessidade de sangue encontrada.</p>
          </div>
        ) : (
          <Row xs={1} md={2} lg={3} className="g-4">
            {necessidades.map((necessidade) => (
              <Col key={necessidade.id}>
                <Card className="h-100 necessidade-card">
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-start mb-3">
                      <Badge bg={getBadgeVariant(necessidade.nivel_urgencia)}>
                        {necessidade.nivel_urgencia}
                      </Badge>
                      <h3 className="tipo-sanguineo">{necessidade.tipo_sanguineo}</h3>
                    </div>
                    
                    <Card.Title>{necessidade.nome_organizacao}</Card.Title>
                    <Card.Text>
                      <strong>Localização:</strong> {necessidade.cidade}, {necessidade.estado}<br />
                      <strong>Quantidade necessária:</strong> {necessidade.quantidade_necessaria}ml<br />
                      <strong>Data limite:</strong> {formatarData(necessidade.data_limite)}<br />
                      {necessidade.observacoes && (
                        <>
                          <strong>Observações:</strong><br />
                          {necessidade.observacoes}
                        </>
                      )}
                    </Card.Text>
                  </Card.Body>
                  <Card.Footer>
                    <Button 
                      variant="outline-primary" 
                      href={`tel:${necessidade.contato}`}
                      className="w-100"
                    >
                      <i className="fas fa-phone-alt me-2"></i>
                      Entrar em contato
                    </Button>
                  </Card.Footer>
                </Card>
              </Col>
            ))}
          </Row>
        )}
      </Container>
    </div>
  );
}

export default ListaNecessidades;
